#http://rosalind.info/problems/subs/

def find_motif(dna_sequence, motif):
    positions = []
    for i in range(len(dna_sequence)):
        if dna_sequence[i:i+len(motif)] == motif:
            positions.append(i+1)
    return positions

dna = ''
sub = 'CTAATGGCT'

print(find_motif(dna, sub))
